//jshint esversion: 6

import React, {
  useState
} from "react";
import Artist from "./Artist";
import Artists from "../Artists";
import Filter from "./Filter";

function App() {
  const [artistsDisplay, setArtistsDisplay] = useState(Artists);

  function sortArtists(sortValue) {

    if (sortValue === "chronological") {
      setArtistsDisplay(() => {
        return Artists.sort((a, b) => {
          return (parseInt(a.years.substring(0, 4)) - parseInt(b.years.substring(0, 4)));
        });
      });
    }
    if (sortValue === "alphabetical") {
      setArtistsDisplay(() => {
        return Artists.sort((a, b) => {
          if (a.name < b.name) {
            return -1;
          }
          if (a.name > b.name) {
            return 1;
          }
          return 0;
        });
      });
    }
  }

  function filterArtists(filterName) {

    setArtistsDisplay((prevValue) => {
      return Artists.filter((artist) => {
        return artist.artMovement === filterName;
      });
    });
  }

  return ( <
    div >
    <
    h1 > Influential Artists Throughout the Ages < /h1> <
    Filter filterArtists = {
      filterArtists
    }
    sortArtists = {
      sortArtists
    }
    /> {
      artistsDisplay.map(Artists => {
        return ( <
          Artist id = {
            Artists.id
          }
          artistImg = {
            Artists.artistImg
          }
          name = {
            Artists.name
          }
          years = {
            Artists.years
          }
          artMovement = {
            Artists.artMovement
          }
          bio = {
            Artists.bio
          }
          />
        );
      })
    } <
    /div>
  );
}

export default App;
