import React from "react";
import Artists from "../Artists";

function Gallery (props) {
    // let artistIndex = props.topWorksIndex;
    let topWorksIndex = Artists.findIndex(p => p.name === props.artistName);
    let imgSrc = Artists[topWorksIndex].topWorks[0].workImg;
    return (
        <div className="artwork-thumbnails">
            <h1>{topWorksIndex}</h1>
            <img className="img-thumbnail" src={imgSrc} alt=""/>
        </div>
    )
}

export default Gallery;
