import React, {useState} from "react";
import FullImage from "./Full Image"

function ArtThumbnail (props) {
    const [isFullScreen, setIsFullScreen] = useState(
        {display: {display: "none"}, 
        isVisible: false}
    );

    function showFullScreen () {
        console.log(props.artWorkTitles);
        console.log(isFullScreen.display)
        setIsFullScreen(
            {display: {display: "absolute"}, 
            isVisible: true}
        );
    }

    function closeFullScreen () {
        setIsFullScreen(
            {display: {display: "none"}, 
            isVisible: false}
        );
    }

    return (
        <div>
        <img className = "img-thumbnail" src={props.artWorkSrc} alt={props.artWorkTitles} onClick={showFullScreen}/>
         {isFullScreen.isVisible ? <FullImage 
            artWorkTitles = {props.artWorkTitles} 
            artWorkSrc = {props.artWorkSrc} 
            artWorkYears = {props.artWorkYears} 
            showFullScreen = {showFullScreen}
            closeFullScreen = {closeFullScreen}
            display = {isFullScreen.display}
            />  
            : null }
        </div>

    )
}

export default ArtThumbnail;
