import React, {useState} from "react";

function Sort (props) {

    const [sortValue, setSortValue] = useState({value: ''});

    function handleSort (event) {
        setSortValue({value: event.target.value});
        props.filterArtists(sortValue.value);
    }

    return(
        <form className="sort">
            <div className="sort-by">
                <h3>Sort by:</h3>
                <div>
                    <input onClick={handleSort} type="checkbox" name="alphabetical"></input>
                    <label htmlFor="Alphabetical">alphabetical</label>
                </div>
                <div>
                    <input onClick={handleSort} type="checkbox" name="chronological"></input>
                    <label htmlFor="chronological">chronological</label>
                </div>
            </div>
        </form>

    )
}

export default Sort;
