import React, {useState} from "react";
import FullImage from "./Full Image"

function ArtThumbnail (props) {
    const [isFullScreen, setIsFullScreen] = useState(
        {display: {display: "none"}, 
        isVisible: false}
    );

    function handleClick() {
        console.log(props.artWorkTitles);
        {props.isVisible ? <FullImage artWorkTitles = {props.artWorkTitles} artWorkSrc = {props.artWorkSrc} artWorkYears = {props.artWorkYears} />  : false }
    }

    return (
        <img className = "img-thumbnail" src={props.artWorkSrc} alt={props.artWorkTitles} onClick={handleClick}/>
    )
}

export default ArtThumbnail;
