import React from "react";

function Sort () {
    return(
        <form className="Sort">
            <div className="sort-by">
                <h3>Sort by:</h3>
                <select onChange={handleSort}>
                    <option value="chronological">Chronological</option>
                    <option value="alphabetical">Alphabetical</option>
                </select>
            </div>
        </form>
    )
}
